sysauto_script.condition:
//create the answer variable
var answer = true;

//Get todays date and determine which day of the week it is
var rightNow = new GlideDateTime();
var dayofweek = rightnow.getdayofweeklocaltime();

//if today is Saturday (6) or Sunday (7), set the anwer variable to false
//so the Run this script fields script does not execute.
//Monday = 1, Tuesday = 2, etc.

if(dayofweek == 6 || dayofweek ==7){
	answer = false;
}

sysauto_script.script:
//Query the database for NeedIt Task records with DueDate field values older
//than the current time. Only return NeedIt Task records that do not have
//a State field value of Closed Complete.

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',rightNow);
overdueNITasks.addQuery('state','!=',3);
overdueNITasks.query();

//Write a longer message for each overdue NeedIt Task record
while(overdueNITask.next()){
		gs.info("Overdue NeedIt Task record: " + overdueNITask.number);
}
